CREATE DATABASE IF NOT EXISTS `bkzone`;
USE bkzone;

-- CHANGE DATABASE ENCODE --
ALTER DATABASE bkzone
	CHARACTER SET = utf8mb4 COLLATE = utf8mb4_unicode_ci;
CREATE TABLE `Customer` (
  `id` varchar(36),
  `first_name` varchar(255),
  `last_name` varchar(255),
  `phone` varchar(255),
  `email` varchar(255),
  `birthday` date,
  `username` varchar(255),
  `password` varchar(255),
  `address` varchar(255),
  PRIMARY KEY (id)
);
CREATE TABLE `Product` (
  `id` varchar(36) ,
  `name` varchar(255),
  `thumbnail` varchar(255),
  `price` integer,
  `quantity` integer,
  `brand` varchar(255),
  `cpu` varchar(255),
  `gpu` varchar(255),
  `ram` varchar(255),
  `disk` varchar(255),
  `screen_size` varchar(255),
  `screen_tech` varchar(255),
  `weight` integer,
  `os` varchar(255),
  `overall_rating` float,
  `num_rates` integer,
  `description` longtext,
  PRIMARY KEY (id)
);

CREATE TABLE `Order` (
  `id` varchar(36),
  `customer_id` varchar(36),
  `order_date` timestamp,
  `status` varchar(255),
  `total_order_money` integer,
  PRIMARY KEY(id)
);

CREATE TABLE `OrderDetail` (
  `order_id` varchar(36),
  `product_id` varchar(36),
  `quantity` integer,
  `total_money` integer,
  PRIMARY KEY (`order_id`, `product_id`)
);

CREATE TABLE `Comment` (
  `id` varchar(36),
  `product_id` varchar(36),
  `customer_id` varchar(36),
  `admin_id` varchar(36),
  `comment` varchar(255),
  `updated_at` timestamp,
  `status` varchar(255),
  PRIMARY KEY (id)
);

CREATE TABLE `Admin` (
  `id` varchar(36),
  `first_name` varchar(255),
  `last_name` varchar(255),
  `phone` varchar(255),
  `email` varchar(255),
  `username` varchar(255),
  `password` varchar(255),
  PRIMARY KEY (id)
);

CREATE TABLE `News` (
  `id` varchar(36),
  `admin_id` varchar(36),
  `thumbnail` varchar(255),
  `content` longtext,
   PRIMARY KEY(id)
);

ALTER TABLE `Order` ADD FOREIGN KEY (`customer_id`) REFERENCES `Customer` (`id`);

ALTER TABLE `OrderDetail` ADD FOREIGN KEY (`order_id`) REFERENCES `Order` (`id`);

ALTER TABLE `OrderDetail` ADD FOREIGN KEY (`product_id`) REFERENCES `Product` (`id`);

ALTER TABLE `Comment` ADD FOREIGN KEY (`product_id`) REFERENCES `Product` (`id`);

ALTER TABLE `Comment` ADD FOREIGN KEY (`customer_id`) REFERENCES `Customer` (`id`);

ALTER TABLE `Comment` ADD FOREIGN KEY (`admin_id`) REFERENCES `Admin` (`id`);

ALTER TABLE `News` ADD FOREIGN KEY (`admin_id`) REFERENCES `Admin` (`id`);

