-- Product
INSERT INTO Product (id, name, thumbnail, price, quantity, brand, cpu, gpu, ram,disk, screen_size, screen_tech, weight, os, overall_rating, num_rates,description)
VALUES ('e74f1011-73c9-11ed-93a1-00155d001934','Apple Ultrabook MacBook Pro 8GB','https://images.fpt.shop/unsafe/filters:quality(5)/fptshop.com.vn/Uploads/images/2015/Tin-Tuc/QuanLNH2/macbook-pro-14-4.JPG',34645760,17,'Apple','Intel Core i5 2.3GHz','Intel Iris Plus Graphics 640','8GB','128GB SSD','13.3','IPS Panel Retina Display 2560x1600',1.37,'macOS',1,184, "MacBook Pro 2021 16 inch được ra mắt để thay thế cho dòng MacBook Pro 2019 16 inch trước đây của Apple. Giờ đây với kích thước màn hình được nâng lên thành 16.2 inch cùng với phần viền mỏng đều ở 3 cạnh đã giúp chiếc MacBook này trở nên hiện đại hơn rất nhiều.Không những thế chất lượng hiển thị năm nay cũng được nâng cấp rất nhiều nhằm tối ưu hoá trải nghiệm thị giác cho người dùng. Apple sử dụng màn hình Mini-LED với tấm nền Liquid Retina XDR display độ phân giải (3024 x 1964), 1 tỷ màu và độ tương phản 1.000.000 :1 sẽ giúp cho màu đen hiển thị sâu hơn, khả năng tái tạo màu sắc tuyệt vời, độ tương phản cao cho màu sắc luôn rực rỡ.Không những thế máy cũng được trang bị công nghệ Promotion thay đổi tần số quét của màn hình từ 10Hz cho đến tối đa 120Hz. Độ sáng cũng được nâng lên 1000 nits và tối đa 1600 nits giúp bạn dù cho làm việc trong môi trường ánh sáng phức tạp cũng hiển thị rõ nét.Bạn sẽ có một màn hình 16.2 inch với 3 viền cực mỏng chỉ 3.5mm bên cạnh đó bạn còn một cái Tai thỏ như trên các dòng iPhone X về sau này. Ở vị trí Tai thỏ đó bạn sẽ có camera 1080p sau nhiều năm Apple vẫn giữ nguyên độ phân giải 720p.");
INSERT INTO Product (id, name, thumbnail, price, quantity, brand, cpu, gpu, ram,disk, screen_size, screen_tech, weight, os, overall_rating, num_rates,description)
VALUES ('e74fb862-73c9-11ed-93a1-00155d001934','Acer Notebook Aspire 3','https://anphat.com.vn/media/product/39134_35206_1.png',29843996,25,'Acer','AMD A9-Series 9420 3GHz','AMD Radeon R5','4GB','500GB HDD','15.6','1366x768',2.1,'Windows',5,312,'Laptop Acer Aspire 3 được thiết kế với chất liệu cao cấp, toát lên vẻ thanh lịch và hiện đại. Bên cạnh đó thiết kế của chiếc laptop này còn được đánh giá thêm là khó gọn nhẹ vời chỉ gần 20 mm chiều dày, giúp cho việc linh động nó dễ dàng và hiệu quả hơn.Laptop Acer Aspire 3 là dòng laptop rẻ cho sinh viên có thể đem đến cho bạn mọi tác vụ từ cơ bản office đến thao tác đòi hỏi cao hơn về cấu hình như thiết kế, dựng phim…Tất cả chính là nhờ vào bộ xử lý intel core i5 1135G7 và card dùng cho đồ họa Intel Iris Xe Graphics. Đi kèm bộ nhớ RAM 8 GB và  256GB GB SSD sẽ hỗ trợ đa nhiệm mượt mà và lưu trữ dữ liệu thoải mái.');
INSERT INTO Product (id, name, thumbnail, price, quantity, brand, cpu, gpu, ram,disk, screen_size, screen_tech, weight, os, overall_rating, num_rates,description)
VALUES ('e7503d15-73c9-11ed-93a1-00155d001934','Apple Ultrabook MacBook Pro 16GB','https://images.fpt.shop/unsafe/filters:quality(5)/fptshop.com.vn/Uploads/images/2015/Tin-Tuc/QuanLNH2/macbook-pro-14-4.JPG',31172929,20,'Apple','Intel Core i5 2.3GHz','Intel Iris Plus Graphics 640','16GB','256GB Flash Storage','15.4','IPS Panel Retina Display 2880x1800',2.04,'macOS',2,758,"MacBook Pro 2021 16 inch được ra mắt để thay thế cho dòng MacBook Pro 2019 16 inch trước đây của Apple. Giờ đây với kích thước màn hình được nâng lên thành 16.2 inch cùng với phần viền mỏng đều ở 3 cạnh đã giúp chiếc MacBook này trở nên hiện đại hơn rất nhiều.Không những thế chất lượng hiển thị năm nay cũng được nâng cấp rất nhiều nhằm tối ưu hoá trải nghiệm thị giác cho người dùng. Apple sử dụng màn hình Mini-LED với tấm nền Liquid Retina XDR display độ phân giải (3024 x 1964), 1 tỷ màu và độ tương phản 1.000.000 :1 sẽ giúp cho màu đen hiển thị sâu hơn, khả năng tái tạo màu sắc tuyệt vời, độ tương phản cao cho màu sắc luôn rực rỡ.Không những thế máy cũng được trang bị công nghệ Promotion thay đổi tần số quét của màn hình từ 10Hz cho đến tối đa 120Hz. Độ sáng cũng được nâng lên 1000 nits và tối đa 1600 nits giúp bạn dù cho làm việc trong môi trường ánh sáng phức tạp cũng hiển thị rõ nét.Bạn sẽ có một màn hình 16.2 inch với 3 viền cực mỏng chỉ 3.5mm bên cạnh đó bạn còn một cái Tai thỏ như trên các dòng iPhone X về sau này. Ở vị trí Tai thỏ đó bạn sẽ có camera 1080p sau nhiều năm Apple vẫn giữ nguyên độ phân giải 720p.");
INSERT INTO Product (id, name, thumbnail, price, quantity, brand, cpu, gpu, ram,disk, screen_size, screen_tech, weight, os, overall_rating, num_rates,description)
VALUES ('e750a798-73c9-11ed-93a1-00155d001934','Asus Ultrabook ZenBook UX430UN','https://fptshop.com.vn/Uploads/Originals/2021/3/1/637502173944633590_asus-vivobook-x415-print-bac-dd.jpg',46780635,13,'Apple','Intel Core i5 2.3GHz','Intel HD Graphics 6000','8GB','256GB Flash Storage','13.3','1440x900',1.34,'macOS',2,915,'Nằm trong Series Zenbook của ASUS, UX430 là sản phẩm mới nhất và được hãng sản xuất đầu tư rất nhiều để đảm bảo trải nghiệm cho khách hàng trên 3 yếu tố theo thứ tự ưu tiên: Hiệu năng – giá thành – độ cơ động. Asus Zenbook UX430UN sở hữu thiết kế đơn giản nhưng trẻ trung và chắc chắn, cấu hình mạnh mẽ với các tính năng mới mà vẫn đáp ứng được nhu cầu di chuyển thường xuyên, đây thực sự là 1 trong những laptop dành cho sinh viên, dân văn phòng cần máy cấu hình cao mà không hề bất tiện trong việc mang đi cũng như sử dụng.');


